import React from 'react';
import earcut from "earcut";

const EarcutTest = () => {
    let canvas = document.getElementById('canvas');
    let context = canvas.getContext('2d');

    // function drawCircle() {
    //     context.beginPath();
    //     context.fillStyle = "#FFF";
    //     context.arc(300, 300, 200, 0, Math.PI * 2);
    //     context.fill();
    //     context.stroke();
    //     context.closePath();
    // }
    // drawCircle()
    let triangles = earcut([10,0, 0,50, 60,60, 70,10]); // returns [1,0,3, 3,2,1]
    let triangles2 = earcut([[300,300],[500,300],[166,449],[277,101],[462,416],[104,343],[397,125],[365,489],[115,222],[480,214],[243,491]]); // returns [1,0,3, 3,2,1]

    console.log(triangles2)
    //let v = triangles.vertices
    for(let i = 0; i < triangles.length; i += 3){
        const t0 = triangles[i * 3 + 0];
        const t1 = triangles[i * 3 + 1];
        const t2 = triangles[i * 3 + 2];
        context.beginPath();
        context.moveTo(triangles[t0 * 2], triangles[t0 * 2 + 1]);
        context.lineTo(triangles[t1 * 2], triangles[t1 * 2 + 1]);
        context.lineTo(triangles[t2 * 2], triangles[t2 * 2 + 1]);
        context.closePath();
        context.stroke();
    }


    // var a = [
    //     [[0,100],[100,100],[100,0],[0,0]], //outer polygon
    //     [[25,25],[75,25],[75,75],[25,75]] //hole
    // ]
    // var toProcess = earcut.flatten(a);
    // var result = earcut(toProcess.vertices, toProcess.holes, toProcess.dimensions);
    // console.log(toProcess)
    // console.log(result)
    return (
        <div>
            Hello!!!
        </div>
    );
};

export default EarcutTest;
