import React, {useEffect, useRef, useState} from "react";
import {Delaunay} from "d3-delaunay";
import './App.css'
import EarcutTest from "./EarcutTest";



function App() {
  let canvas = document.getElementById('canvas');
  let context = canvas.getContext('2d');

  let pointsForRectangle = [];
  let pointsForCircle = [];
  //let jsonPoints = null;
  //let jsonTrianglesPoints = null;

  function triangulationRectangle(stepRectangle) {
    for (let x = 0; x <= 600; x += stepRectangle) {
      for (let y = 0; y <= 300; y += stepRectangle) {
        pointsForRectangle.push([x , y ]);//x+600, y+150
      }
    }
  }


// Функция для добавления точек в круг
  function triangulationCircle(stepСircle) {
    let radius = 200;
    let x = null;
    let y = null;
    pointsForCircle.push([300, 300]);
    // for (let phi = 0; phi <= 360; phi += stepСircle) {
    //     for (let radius = 200; radius >= 100; radius /= 2) {
    //         x = Math.abs(Math.floor(radius * Math.cos(phi)) + 300);
    //         y = Math.abs(Math.floor(radius * Math.sin(phi)) + 300);
    //         pointsForCircle.push([x, y]);
    //     }
    // }
    for (let phi = 0; phi <= 360; phi += stepСircle) {
      x = Math.abs(Math.floor(radius * Math.cos(phi)) + 300);
      y = Math.abs(Math.floor(radius * Math.sin(phi)) + 300);
      pointsForCircle.push([x, y]);
      console.log(([x, y]))
    }
    // for (let x = 0; x <= 200; x += 100) {
    //     for (let y = 0; y <= 200; y += 100) {
    //         pointsForCircle.push([x+200 , y+200 ]);
    //     }
    // }
    drawCircle()
  }

// Нарисовать круг
  function drawCircle() {
    context.beginPath();
    context.fillStyle = "#FFF";
    context.arc(300, 300, 200, 0, Math.PI * 2);
    context.fill();
    context.stroke();
    context.closePath();
  }

  // Состояние JSON
  const [jsonPoints, setJsonPoints] = useState('')
  const [jsonVerticesOfTriangles, setJsonVerticesOfTriangles] = useState('')


  useEffect(() => {

  }, [])


// Триангуляция
  function triangulation(arr) {
    const delaunay = Delaunay.from(arr);
    let triangles = delaunay.triangles
   let points = delaunay.points;
    // let inedges = delaunay.inedges
    // console.log(inedges)
    //console.log(triangles)
    console.log(points)
    /**/
    for (let i = 0; i < triangles.length/3; i++) {
      const {points, triangles} = delaunay;
      const t0 = triangles[i * 3 + 0];
      const t1 = triangles[i * 3 + 1];
      const t2 = triangles[i * 3 + 2];
      context.beginPath();
      context.moveTo(points[t0 * 2], points[t0 * 2 + 1]);
      context.lineTo(points[t1 * 2], points[t1 * 2 + 1]);
      context.lineTo(points[t2 * 2], points[t2 * 2 + 1]);
      context.closePath();
      context.stroke();
    }

    let coordinates = [];
    for (let i = 0; i < triangles.length/3; i += 1) {
        const t0 = triangles[i * 3 + 0];
        const t1 = triangles[i * 3 + 1];
        const t2 = triangles[i * 3 + 2];
        coordinates.push(
          [points[[t0 * 2]], points[[t0 * 2 + 1]],
          points[[t1 * 2]], points[t1 * 2 + 1],
          points[[t2 * 2]], points[t2 * 2 + 1]]
        );
    }
    console.log(coordinates)
    // let line1,line2,line3;
    // for (let i = 0; coordinates.length; i++){
    //  const a = coordinates.shift();
    //  console.log(a)
    //   console.log(coordinates)
    // }

   let newArr = coordinates.map(elementArr => {
     let a, b, c, Xa, Ya, Xb, Yb, Xc, Yc;

     const arr = [];
     a = Math.abs(Math.sqrt(Math.pow((elementArr[2] - elementArr[0]), 2) + Math.pow((elementArr[3] - elementArr[2]), 2)));
     b = Math.abs(Math.sqrt(Math.pow((elementArr[4] - elementArr[0]), 2) + Math.pow((elementArr[5] - elementArr[1]), 2)));
     c = Math.abs(Math.sqrt(Math.pow((elementArr[4] - elementArr[2]), 2) + Math.pow((elementArr[5] - elementArr[3]), 2)));
     arr.push(a)
     arr.push(b)
     arr.push(c)
     return arr;
   })
    console.log(newArr)




    //triangulationQuality(coordinatesArr)
    setJsonPoints(JSON.stringify(arr))
    setJsonVerticesOfTriangles(JSON.stringify(triangles))
  }


  const [title, setTitle] = useState(40)
  const [title2, setTitle2] = useState(40)


  // Кнопка триангуляции прямоугольника
  function triangulationRectangleButton(e) {

    e.preventDefault();
    context.clearRect(500, 100, 800, 600);
    triangulationRectangle(Number(title))
    triangulation(pointsForRectangle)

    pointsForRectangle = [];
  }

  // Кнопка триангуляции круга
  function triangulationCircleButton(e) {

    e.preventDefault();
    triangulationCircle(Number(title2))
    triangulation(pointsForCircle)

    pointsForCircle = [];
  }

  // кнопка триангуляции полигона
  function triangulationPolygonButton(e) {
    context.clearRect(0, 0, canvas.width, canvas.height);
    let arrPointsPolygon = [[0, 0], [500, 0], [500, 500], [400, 500], [400, 250], [100, 250], [100, 500], [0, 500]]
    e.preventDefault();
    triangulation(arrPointsPolygon)
  }

// функция для оценки качества сетки(подсчёт длины треугольника по трём сторонам)
function triangulationQuality(verticesTriangleArr) {
  let a, b, c, x1, x2, x3, y1, y2, y3;
  a = Math.abs(Math.sqrt(Math.pow((x2 - x1), 2) + Math.pow((y2 - y1), 2)));
  b = Math.abs(Math.sqrt(Math.pow((x3 - x2), 2) + Math.pow((y3 - y2), 2)));
  c = Math.abs(Math.sqrt(Math.pow((x3 - x1), 2) + Math.pow((y3 - y1), 2)));
}



  return (
      <div className='App'>
        <form>
          <input
              value={title}
              onChange={e => setTitle(e.target.value)}
              type='number'
          />
          <button onClick={triangulationRectangleButton}>Триангуляция прямоугольника</button>
          <div>
            <input
                value={title2}
                onChange={e => setTitle2(e.target.value)}
                type='number'
            />
            <button onClick={triangulationCircleButton}>Триангуляция круга</button>
          </div>
          <div>
            <button onClick={triangulationPolygonButton}>Триангуляция многоугольника</button>
          </div>
        </form>
        <div>
          {/*<div>*/}
          {/*  <div>JSON</div>*/}
          {/*  <div>Координаты точек</div>*/}
          {/*  <div>{jsonPoints}</div>*/}
          {/*  <div>Номера вершин треугольников</div>*/}
          {/*  <div>{jsonVerticesOfTriangles}</div>*/}
          {/*</div>*/}

        </div>

        <EarcutTest/>

      </div>

  );
}

export default App;
