function App() {
  let canvas = document.getElementById('canvas');
  let context = canvas.getContext('2d');

  let pointsForRectangle = [];
  let pointsForCircle = [];
  //let jsonPoints = null;
  //let jsonTrianglesPoints = null;

  function triangulationRectangle(stepRectangle) {
    for (let x = 0; x <= 600; x += stepRectangle) {
      for (let y = 0; y <= 300; y += stepRectangle) {
        pointsForRectangle.push([x , y ]);//x+600, y+150
      }
    }
  }


// Функция для добавления точек в круг
  function triangulationCircle(stepСircle) {
    let radius = 200;
    let x = null;
    let y = null;
    pointsForCircle.push([300, 300]);
    // for (let phi = 0; phi <= 360; phi += stepСircle) {
    //     for (let radius = 200; radius >= 100; radius /= 2) {
    //         x = Math.abs(Math.floor(radius * Math.cos(phi)) + 300);
    //         y = Math.abs(Math.floor(radius * Math.sin(phi)) + 300);
    //         pointsForCircle.push([x, y]);
    //     }
    // }
    for (let phi = 0; phi <= 360; phi += stepСircle) {
      x = Math.abs(Math.floor(radius * Math.cos(phi)) + 300);
      y = Math.abs(Math.floor(radius * Math.sin(phi)) + 300);
      pointsForCircle.push([x, y]);
      console.log(([x, y]))
    }
    // for (let x = 0; x <= 200; x += 100) {
    //     for (let y = 0; y <= 200; y += 100) {
    //         pointsForCircle.push([x+200 , y+200 ]);
    //     }
    // }
    drawCircle()
  }

// Нарисовать круг
  function drawCircle() {
    context.beginPath();
    context.fillStyle = "#FFF";
    context.arc(300, 300, 200, 0, Math.PI * 2);
    context.fill();
    context.stroke();
    context.closePath();
  }

  // Состояние JSON
  const [jsonPoints, setJsonPoints] = useState('')
  const [jsonVerticesOfTriangles, setJsonVerticesOfTriangles] = useState('')


  useEffect(() => {

  }, [])


// Триангуляция
  function triangulation(arr) {
    const delaunay = Delaunay.from(arr);
    let triangles = delaunay.triangles
   let points = delaunay.points;
    let inedges = delaunay.inedges
     console.log(inedges)
    console.log(triangles)
    console.log(points)
    /**/
    const verticesTriangleArr = [];
    for (let i = 0; i < triangles.length; i++) {
      const {points, triangles} = delaunay;
      const t0 = triangles[i * 3 + 0];
      const t1 = triangles[i * 3 + 1];
      const t2 = triangles[i * 3 + 2];
      context.beginPath();
      console.log("Координаты треугольника")
      context.moveTo(points[t0 * 2], points[t0 * 2 + 1]);

      console.log("Первая точка")
      console.log("X - ",points[t0 * 2],"Y -", points[t0 * 2 + 1])

      console.log("Вторая точка")
      context.lineTo(points[t1 * 2], points[t1 * 2 + 1]);
      console.log("X - ",points[t1 * 2],"Y -", points[t1 * 2 + 1])
      console.log("Третья точка")
      context.lineTo(points[t2 * 2], points[t2 * 2 + 1]);
      console.log("X - ",points[t2 * 2],"Y -", points[t2 * 2 + 1])
      context.closePath();
      context.stroke();
      // verticesTriangleArr.push([
      //     [points[t0 * 2], points[t0 * 2 + 1]],
      //   [points[t1 * 2], points[t1 * 2 + 1]],
      //   [points[t2 * 2], points[t2 * 2 + 1]],])
    }
// FUCKING BULLSHIT где-то ошибка и появляется массивы с undefined
    let coordinates = [];
    for (let i = 0; i < triangles.length; i += 1) {
      console.log(triangles.length)
        const t0 = triangles[i * 3 + 0];
        const t1 = triangles[i * 3 + 1];
        const t2 = triangles[i * 3 + 2];
        coordinates.push(
          points[[t0 * 2]], points[[t0 * 2 + 1]],
          points[[t1 * 2]], points[t1 * 2 + 1],
          points[[t2 * 2]], points[t2 * 2 + 1]
        );
    }
   // coordinates.filter(Boolean)
    console.log(coordinates)

    // for (let i = 0; i < triangles.length; i += 3) {
